#define MACHINE_FLAGS MACHINE_NOT_WORKING|MACHINE_CLICKABLE_ARTWORK
#define MACHINE_FLAGS_NOSOUND MACHINE_FLAGS|MACHINE_NO_SOUND

bool compare_mbus(UINT16* rom)
{
	for (int i=0;i<11;i++)
	{
		if (rom[0] != 0xb109)
			return false;

		if (rom[3] != 0x6612)
			return false;

		if (rom[7] != 0x0c81)
			return false;

		if (rom[10] != 0x66ea)
			return false;
	}

	return true;
}

void sc4_state::find_mbus(UINT16* rom)
{
	for (int i=0;i<(0x100000-0x40)/2;i++)
	{
		bool found = compare_mbus(&rom[i]);

		if (found==true)
		{
			printf("x found at %08x\n", i*2);
			m_chk41addr = i*2;
		}
	}
}


