void peplus_state::handle_lightpen()
{
	int x_val = m_touch_x.read_safe(0x00);
	int y_val = m_touch_y.read_safe(0x00);
	const rectangle &vis_area = m_screen->visible_area();

	int xt = x_val * vis_area.width() / 1024 + vis_area.min_x;
	int yt = y_val * vis_area.height() / 1024 + vis_area.min_y;

	m_assert_lp_timer->adjust(m_screen->time_until_pos(yt, xt), 0);
}

void peplus_state::crtc_vsync(int state)
{
	m_maincpu->set_input_line(0, state ? ASSERT_LINE : CLEAR_LINE);
	handle_lightpen();
}

template<uint8_t Which>
void peplus_state::paldata_w(uint8_t data)
{
	m_paldata[Which] = data;
}

void peplus_state::crtc_display_w(uint8_t data)
{
	m_videoram[m_vid_address] = data;
	m_palette_ram[0][m_vid_address] = m_paldata[0];
	m_palette_ram[1][m_vid_address] = m_paldata[1];

	m_bg_tilemap->mark_tile_dirty(m_vid_address);

	// An access here triggers a device read !
	m_crtc->register_r();
}

void peplus_state::duart_w(uint8_t data)
{
	// Used for Slot Accounting System Communication
}

void peplus_state::cmos_w(offs_t offset, uint8_t data)
{
	char bank_name[6];

	// Test for Wingboard PAL Trigger Condition
	if (offset == 0x1fff && m_wingboard && data < 5)
	{
		sprintf(bank_name, "user%d", data + 1);
		load_superdata(bank_name);
	}

	m_cmos_ram[offset] = data;
}

void peplus_state::output_bank_a_w(uint8_t data)
{
/*
bits
7654 3210
---- ---x Coin Lockout
---- --x- Diverter
---- -x-- Bell
---- x--- N/A
---x ---- Hopper 1
--x- ---- Hopper 2
-x-- ---- specific to a kind of machine
x--- ---- specific to a kind of machine
*/
	for (uint8_t i = 0; i < 8; i++)
		m_bnka[i] = BIT(data, i);

	m_coin_out_state = 0;
	if(((data >> 4) & 1) || ((data >> 5) & 1))
		m_coin_out_state = 3;
}

void peplus_state::output_bank_b_w(uint8_t data)
{
/*
bits
7654 3210
---- ---x specific to a kind of machine
---- --x- Deal Spin Start
---- -x-- Cash Out
---- x--- specific to a kind of machine
---x ---- Bet 1 / Bet Max
--x- ---- Change Request
-x-- ---- Door Open
x--- ---- specific to a kind of machine
*/
	for (uint8_t i = 0; i < 8; i++)
		m_bnkb[i] = BIT(data, i);
}

void peplus_state::output_bank_c_w(uint8_t data)
{
/*
bits
7654 3210
---- ---x Coin In Meter
---- --x- Coin Out Meter
---- -x-- Coin Drop Meter
---- x--- Jackpot Meter
---x ---- Bill Acceptor Enabled
--x- ---- SDS Out
-x-- ---- N/A
x--- ---- Game Meter
*/
	for (uint8_t i = 0; i < 8; i++)
		m_bnkc[i] = BIT(data, i);

	m_bv_enable_state = (data >> 4) & 1;
}

void peplus_state::i2c_nvram_w(uint8_t data)
{
	m_i2cmem->write_scl(BIT(data, 2));
	m_sda_dir = BIT(data, 1);
	m_i2cmem->write_sda(BIT(data, 0));
}


